mapper.select:
select leave_info_id as leaveInfoId, leave_info_date as leaveInfoDate, leave_info_mask as leaveInfoMask, distribution_member_id as distributionMemberId, create_time as createTime, create_id as createId, last_update_time as lastUpdateTime, last_update_id as lastUpdateId from zlogistics.member_leave_info
is_deleted = 0
and distribution_member_id = #{distributionMemberId}
and leave_info_date = #{leaveInfoDate}
limit #{firstRow},#{pageRows}

select 
	count(*)
from zlogistics.member_leave_info 

	is_deleted = 0

		and distribution_member_id = #{distributionMemberId}


		and leave_info_date = #{leaveInfoDate}
