mapper.select:
SELECT menu_id AS menuId, menu_name AS menuName, menu_url AS menuUrl, menu_father AS menuFather, menu_order as menuOrder, create_time as createTime, update_time as lastUpdateTime FROM zlogistics.menu t1
t1.is_deleted = 0,t1.update_time = now()
and t1.menu_name like concat('%',#{menuName},'%')
and t1.menu_url like concat('%',#{menuUrl},'%')
and t1.menu_order =#{menuOrder}
and t1.menu_father =#{menuFather}
limit #{firstRow},#{pageRows}

 SELECT
		count (*)
		FROM zlogistics.menu t1

	t1.is_deleted = 0

		and t1.menu_name like concat('%',#{menuName},'%')


		and t1.menu_url like concat('%',#{menuUrl},'%')


		and t1.menu_order =#{menuOrder}


		and t1.menu_father =#{menuFather}



limit #{firstRow},#{pageRows}
