mapper.select:
	SELECT
  COUNT(DISTINCT t1.distribution_member_id)
FROM zlogistics.member_schedule t1
WHERE t1.is_deleted = 0
    AND t1.distribution_station_id = #{distributionStationId}
    AND t1.schedule_date = #{scheduleDate}

select shift_schedule_id as shiftScheduleId, distribution_station_name as distributionStationName, distribution_station_id as distributionStationId, schedule_name as scheduleName, schedule_id as scheduleId, schedule_date as scheduleDate, distribution_member_names as distributionMemberNames, create_time as createTime, create_id as createId, last_update_time as lastUpdateTime, last_update_id as lastUpdateId from zlogistics.shift_schedule t1
is_deleted = 0
and distribution_station_id = #{distributionStationId}
and schedule_date = #{scheduleDate}
limit #{firstRow},#{pageRows}

select 
	count(*)
from zlogistics.shift_schedule 

	is_deleted = 0

		and distribution_station_id = #{distributionStationId}


		and schedule_date = #{scheduleDate}
