mapper.select:
select t1.user_id as userId, t1.user_name as userName, t1.name as name, t1.station_id as stationId, t1.user_password as passWord, t1.role_id as roleId, t4.role_name as roleName, t1.city_id as cityId, t1.create_time as createTime, t1.create_id as createId, t1.mobile as mobile, t1.last_update_time as lastUpdateTime, t1.last_update_id as lastUpdateId, t1.is_deleted from zlogistics.user t1 left join zlogistics.distribution_station t2 on t1.station_id =t2.distribution_station_id left join zlogistics.city t3 on t1.city_id = t3.city_id left join zlogistics.role t4 on t4.role_id = t1.role_id
t1.is_deleted = 0
and t1.user_name like concat('%',#{userName},'%')
and t1.name like concat('%',#{name},'%')
and t1.user_id =#{userId}
and t1.city_id = #{cityId}
and t1.station_id = #{stationId}
limit #{firstRow},#{pageRows}

select count(*) from zlogistics.user t1 join zlogistics.distribution_station t2 on t1.station_id = t2.distribution_station_id join zlogistics.city t3 on t1.city_id = t3.city_id left join zlogistics.role t4 on t4.role_id = t1.role_id
t1.is_deleted = 0
and t1.user_name like concat('%',#{userName},'%')
and t1.name like concat('%',#{name},'%')
and t1.user_id =#{userId}
and t1.city_id = #{cityId}
and t1.station_id = #{stationId}
limit #{firstRow},#{pageRows}

mapper.update:
update zlogistics.user


		name = #{name}


		,city_id = #{cityId}


		,station_id = #{stationId}


		,mobile = #{mobile}


		,user_password = password(#{passWord})


		,role_id= #{roleId}


		,is_deleted = #{isDeleted}


where user_id = #{userId}
